mapper.update:
update category
name = #{name}, sort = #{sort}, `type` = #{type}, `status` = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select id, type, name, sort, status, create_time, update_time, create_user, update_user from category
and `name` like concat('%', #{name}, '%') and `type` = #{type}
order by sort

select id, type, name, sort from category
type = #{type}
order by sort
