mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, `description` = #{description}, `status` = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id} and `status` = 0

mapper.select:
select count(*) from dish

    and category_id = #{categoryId}
    and status = #{status}

select d.id, d.name, d.category_id, d.price, d.image, d.status, d.description, d.update_time, c.name categoryName from dish d left join category c on d.category_id = c.id
and d.category_id = #{categoryId}
and d.name like concat('%', #{name}, '%')
and d.status = #{status}
order by d.create_time desc

select d.id, d.name, d.price, d.description, d.status
from dish d
where d.category_id = #{categoryId}

select d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.update_time, df.id df_id, df.dish_id, df.name df_name, df.value from dish d left join dish_flavor df on d.id = df.dish_id
and d.category_id = #{categoryId}
and d.status = #{status}
