mapper.select:
select count(*) from setmeal


        and category_id = #{categoryId}


        and `status` = #{status}

select s.id, s.category_id, s.`name`, s.price, s.`status`, s.description, s.image, s.update_time, c.name categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%', #{name}, '%')
and s.category_id = #{categoryId}
and s.status = #{status}

select s.id, s.category_id, s.`name`, s.price, s.`status`, s.description, s.image, s.update_time from setmeal s
and s.category_id = #{categoryId}
and s.status = #{status}
